mapper.insert:
INSERT INTO t_feed
SET iuser = #{iuser}

    , contents = #{contents}


    , location = #{location}

mapper.select:
SELECT A.ifeed, A.contents, A.location, B.iuser AS writerIuser , B.nm AS writerNm, A.created_at AS createdAt, B.pic AS writerPic , if(C.ifeed IS NULL, 0, 1) AS isFav FROM t_feed A JOIN t_user B ON A.iuser = B.iuser LEFT JOIN t_feed_fav C ON A.ifeed = C.ifeed AND B.iuser = #{loginedIuser}
WHERE A.iuser = #{targetIuser}
ORDER BY A.ifeed DESC LIMIT #{startIdx}, #{rowCount}
